import styled from "styled-components"

const Container = styled.div`
height:30px;
background-color: teal;
color:white;
display: flex;
align-items:center;
justify-content: center;
font-size: 14px;
`


const Announcement = () => {
  return (
    <Container>
        Black Friday deal! Free shipping on all orders!</Container>
  )
}

export default Announcement
